import './Post.css';

import { useState } from "react";

import axios from 'axios';

const Post = (props) => {

    const [likesCount, setLikesCount] = useState(props.post.likes.length)



    const deletePost = (id) => {
        axios.post('https://akademia108.pl/api/social-app/post/delete',{
        post_id: id
        }).then(res=>{

        })
        .catch((error) => {
            console.error(error);
        });
    };

    return (
        <div className="post">
            <div className="avatar">
                <img src={props.post.user.avatar_url} alt={props.post.user.username} />
            </div>
            <div className="postData">
                <div className="postMeta">
                    <div className="author">{props.post.user.username}</div>
                    <div className="date">{props.post.created_at.substring(0, 10)}</div>
                </div>
                <div className="postContent">{props.post.content}</div>
                <div className="likes">
                   {props.user?.username === props.post.user.username && <button className='btn'>Delete</button>}
                    {likesCount}

                </div>
            </div>

         <div className='deleteConfirmation'>
            <h3>Are you sure you want to delete post?</h3>
            <button className='btn yes'>Yes</button>
            <button className='btn no'>No</button>
         </div>

        </div>
    );
}

export default Post;
