    })
        .then((req) => {
            setPosts(posts.concat(req.data));
        })
        .catch((error) => {
            console.error(error);
        });

    useEffect(() => {
        getLatestPosts();
    }, [props.user]);

    return (
        <div className="home">
            <div className="postList">
                {posts.map(post => {
                    return <Post post={post} key={post.id} />
                })}
                <button className='btn loadMore' onClick={getNextPosts}>Load more</button>
            </div>
        </div>


    )};


export default Home;
